        Update-AppStatus("Fetching sessions from $($tvPools.SelectedNode.Text)")
        $UserSessionsRet = Fill-Sessions($tvPools.SelectedNode.Text)

        Update-AppStatus("Connected to $($tvPools.SelectedNode.Text)")
